mapper.select:
SELECT

FROM medicine a

WHERE a.mid = #{mid}

mapper.update:
        UPDATE medicine SET
            mname = #{mname}
            ,mshortname = #{mshortname}
            ,mnors = #{mnors}
            ,marea = #{marea}
            ,mlot = #{mlot}
            ,minpri = #{minpri}
            ,mwpri = #{mwpri}
            ,mpri = #{mpri}
            ,mnum = #{mnum}
            ,mcon = #{mcon}
            ,mcate = #{mcate}
            ,jtime = #{jtime}
            ,musetime = #{musetime}
            ,memo = #{memo}
		WHERE mid = #{mid}
